package me.aleiv.core.paper.tablist;

import com.mojang.authlib.properties.Property;

public class TabEntry implements Cloneable {
    public static final Property DEFAULT_SKIN = new Property("textures",
            "",
            "");

    public String text = "";

    public int ping = 50;

    public Property head;

    public TabEntry() {
        this.head = DEFAULT_SKIN;
    }

    public TabEntry(String text) {
        this.head = DEFAULT_SKIN;
        this.text = text;
    }

    public TabEntry getText(String text) {
        this.text = text;
        return this;
    }

    public TabEntry setPing(int ping) {
        this.ping = ping;
        return this;
    }

    public TabEntry setHead(Property head) {
        this.head = head;
        return this;
    }

    public TabEntry removeHead() {
        return setHead(DEFAULT_SKIN);
    }

    public TabEntry clone() {
        return new TabEntry(text).setPing(ping).setHead(head);
    }
}
